import React, { useEffect, useState, useRef, ReactElement } from 'react';
import classNames from 'classnames';

import logoSrc from '@/assets/images/logo.png';
import { NavLink } from '@/components/common';
import './styles.scss';

interface IProps {
  isOpaque?: boolean;
}

function Header(props: IProps): ReactElement {
  const [isFixed, setIsFixed] = useState<boolean>(false);
  const ref = useRef<HTMLHeadingElement>(null);

  useEffect(() => {
    const onScroll = () => {
      const breakpoint = ref.current?.clientHeight ?? 85;
      setIsFixed(window.pageYOffset > breakpoint + 40);
    };

    window.addEventListener('scroll', onScroll);

    return () => {
      window.removeEventListener('scroll', onScroll);
    };
  }, []);

  return (
    <header ref={ref} className={classNames('header', {
      'header_fixed': isFixed,
      'header_opaque': props.isOpaque,
    })}>
      <NavLink to="/" shouldUseActiveStyles={false}>
        <img src={logoSrc} alt="Logo" />
      </NavLink>
      <nav className="header__menu">
        <ul className="header__menu-items">
          <li className="header__menu-item">
            <NavLink
              className="header__nav-link"
              activeClassName="header__nav-link_active"
              to="/"
            >
              Home
            </NavLink>
          </li>
          <li className="header__menu-item">
            <NavLink
              className="header__nav-link"
              activeClassName="header__nav-link_active"
              to="/courses"
            >
              Courses
            </NavLink>
          </li>
          <li className="header__menu-item">
            <NavLink
              className="header__nav-link"
              activeClassName="header__nav-link_active"
              to="/pages"
            >
              Pages
            </NavLink>
          </li>
          <li className="header__menu-item">
            <NavLink
              className="header__nav-link"
              activeClassName="header__nav-link_active"
              to="/about"
            >
              About
            </NavLink>
          </li>
          <li className="header__menu-item">
            <NavLink
              className="header__nav-link"
              activeClassName="header__nav-link_active"
              to="/blog"
            >
              Blog
            </NavLink>
          </li>
          <li className="header__menu-item">
            <NavLink
              className="header__nav-link"
              activeClassName="header__nav-link_active"
              to="/contacts"
            >
              Contacts
            </NavLink>
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
