import React, { ReactElement } from 'react';
import { RouteComponentProps } from '@reach/router';

import { Head } from '@/components/common';
import { Layout } from '@/components/layouts';
import courses from '@/mock-files/courses.json';
import courseImageSrc1 from '@/assets/images/courses/1.png';
import courseImageSrc2 from '@/assets/images/courses/2.png';
import courseImageSrc3 from '@/assets/images/courses/3.png';
import courseImageSrc4 from '@/assets/images/courses/4.png';
import courseImageSrc5 from '@/assets/images/courses/5.png';
import courseImageSrc6 from '@/assets/images/courses/6.png';
import { CourseCard } from './components/course-card';
import './styles.scss';

const images = [
  courseImageSrc1,
  courseImageSrc2,
  courseImageSrc3,
  courseImageSrc4,
  courseImageSrc5,
  courseImageSrc6,
];

function CoursesPage(props: RouteComponentProps): ReactElement {
  return (
    <Layout>
      <Head
        title="List of Courses"
        description="Courses available on the platform"
      />
      <main>
        <section className="courses-page__content">
          {courses.map((course) => (
            <CourseCard
              key={course.id}
              className="courses-page__course-card"
              course={course.course}
              title={course.title}
              imageSrc={images[Number(course.id) - 1]}
            />
          ))}
        </section>
      </main>
    </Layout>
  );
}

export default CoursesPage;
